export const ADD_FAVORITE="ADD_FAVORITE";
export const RMV_FAVORITE="RMV_FAVORITE"


export const addFavorite=(character)=>{
    return {
        type: ADD_FAVORITE,
        payload:character
    }
}


export const rmvFavorite=(id)=>{
    return {
        type: RMV_FAVORITE,
        payload:id
    }
}
