import styled from "styled-components";

export const FormStyle =styled.form`
color: white;
background-color: grey;
height: 300px;
display: flex;
flex-direction: column;
padding-top: 25%;
`

export const LabelForm=styled.label`
width: 30%;
`

export const InputForm=styled.input`
width: 30%;
`
