import Card from './Card';

export default function Cards(props) {
   const { characters } = props;
   return <div>
      {characters.map(({name,species,gender,image})=>{
         return <Card 
         onClose={() => window.alert('Emulamos que se cierra la card')}
         name={name}
         species={species}
         gender={gender}
         image={image}
         />
      })}
   </div>;
}
