export const validate = (userData, setErrors, errors) => {
    if (!userData.username) setErrors({ ...errors, username: "username vacío" });
    else {
      if (/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(userData.username))
        setErrors({ ...errors, username: "" });
      else setErrors({ ...errors, username: "username inválido" });
    }
    if (!userData.password) setErrors({ ...errors, password: "password vacío" });
    else {
      if (/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(userData.username))
        setErrors({ ...errors, username: "" });
      else setErrors({ ...errors, username: "username inválido" });
    }
  };
