import styled from "styled-components";

export const BackgroundRoot = styled.div`
  background-image: url("https://rb.gy/zhak3r");
  width: 100%;
  display: flex;
  flex-direction: column;
`;

export const CardBorder =styled.div`
  border-radius: 30px;
  background-color: white;
  width: 20%;
  padding-bottom: 20px;
`;
